            s1 = prefix_sums[i]
            s2 = prefix_sums[j]

            # Check if s1+s2 is even and palindrome-like
            total_sum = s1 + s2
            if total_sum % 2 != 0:
                continue

            total_sum //= 2
            if_palindrome = True

            # Check if palindrome-like condition holds
            for k in range(1, total_sum + 1):
                if k > total_sum or k < 0:
                    continue

                if k == total_sum:
                    if s1 != s2:
                        continue
                else:
                    if s1 != s2 + k * (k - 1) // 2:
                        continue
                    if s1 != s2 - k * (k - 1) // 2:
                        continue

                if_palindrome = False
                break

            if not is_palindrome(s1, s2, total_sum, is_palindrome):
                continue

            print("Yes")
            return

