    data = sys.stdin.read().splitlines()
    if data:
        n = int(data[0])
        grid = []
        for i in range(1, 1 + n):
            grid.append(data[i].strip())

        # Create original grid
        original_grid = []
        for i in range(n):
            row = []
            for j in range(n):
                row.append(grid[j][i])
            original_grid.append(row)

        # Create result grid
        result_grid = []
        for i in range(n):
            row = []
            for j in range(n):
                if (i == 0 or i == n - 1) and (j == 0 or j == n - 1):
                    # Outer square, shift to inner
                    if i == 0:
                        if j == 0:
                            row.append(original_grid[n-1][n-1])
                        elif j == n - 1:
                            row.append(original_grid[n-1][0])
                        else:
                            row.append(original_grid[n-1][j-1])
                    elif i == n - 1:
                        if j == 0:
                            row.append(original_grid[0][n-1])
                        elif j == n - 1:
                            row.append(original_grid[0][0])
                        else:
                            row.append(original_grid[0][j+1])
                else:
                    # Inner square, keep as is
                    row.append(original_grid[i][j])
            result_grid.append(''.join(row))

        # Output result grid
        for line in result_grid:
            print(line)

